import React, { createContext, useState } from 'react';
import { v4 as uuidv4 } from 'uuid';

export const MovieContext = createContext();

const MovieContextProvider = (props) => {
    const [movies, setMovies] = useState([
        { title: "Star Wars", director: "George Lucas", id: 1 },
        { title: "The Godfather", director: "Mario Puzo", id: 2 },
        { title: "Transformer", director: "Micheal Bay", id: 3 }
    ]);

    const addMovie = (title, director) => {
        setMovies([...movies, { title, director, id: uuidv4() }])
    };

    const removeMovie = (id) => {
        setMovies(movies.filter(movie => movie.id !== id))
    };

    return (
        <MovieContext.Provider value={{ movies, addMovie, removeMovie }}>
            {props.children}
        </MovieContext.Provider>
    )

}

export default MovieContextProvider;
